import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';
import db from "../../db.json"
import ItemCarrito from "./ItemCarrito"

function Carrito() {

    const carrito = db.Carrito.productos
    const productos= db.Ropa
    let [totalCarrito, setTotalCarrito] = useState();

    useEffect(() => {
        carrito.map((info) => {
            const id = info[0]
            const producto = productos[id]


            console.log("id",id)

            console.log("soy carrito", producto)

            console.log("soy la data", producto.precio)


            setTotalCarrito(totalCarrito + (info[1] * (producto.precio)))
            console.log ("total carrito", totalCarrito)
        } )
    }, []);

    return (
        <>
            <div>
                {carrito.map((info, i) => (
                    <div key={i}>
                        <ItemCarrito prenda={info} />
                    </div>
                ))}
            </div>
            <div>
                <p> precio total: {totalCarrito} </p>
            </div>
        </>
    );
}

export default Carrito;
