import React from "react";
import { Link } from "react-router-dom";
import "../../Css/ItemList.css"

const Item = ({ prenda }) => {

    return (
        <div>
            <p> {prenda.nombre}</p>
            <p> precio: {prenda.precio}$</p>
            <img
                className="remera"
                src={prenda.imagen}
            />
            <br />
        </div>
    )
}
export default Item;
