import Item from "./Item";
import React from "react";
import { Col, Row } from "react-bootstrap";
import { Link } from "react-router-dom";
import "../Css/ItemList.css"

const ItemList = (data) => {
    console.log("Estos son los productos", data.productos)
    const laRopa = data.productos



    const loRetornable = laRopa.map((prenda) => {
        return (
            <Col md="4" mb="2">
                    <p> {prenda.nombre}</p>
                    <p> precio: {prenda.precio}$</p>
                <img
                    className="remera"
                    src={prenda.imagen}
                />
                <Link to={"/ropa"}>
                    <button>ver detalel del producto</button>
                </Link> 
            </Col>
        )
    })

    return (
        <Row className="justify-content-md-center">
            {loRetornable}
        </Row>
    )

}
export default ItemList;
