import React from "react";
import { Col, Row } from "react-bootstrap";
import { Link } from "react-router-dom";
import "../Css/ItemList.css"

const Item = ({data}) => {
    console.log("Esto es la data", data)

    return (
            <div>
                {data.map((prenda) => (
                <Col md="4" mb="2">
                    <div>
                        <p> {prenda.nombre}</p>
                        <p> precio: {prenda.precio}$</p>
                        <img
                            className="remera"
                            src={prenda.imagen}
                        />
                        <br />
                        {/* <Link to={"/ropa"}> */}
                        <button>ver detalel del producto</button>
                        {/* </Link> */}
                    </div>
                    </Col>
                ))}
            </div>
    )
}
export default Item;
