import React from 'react';
import "./Navbar.css"

function Contador() {

    let number = 0

    const suma = () => {
        number--
    }

    const resta = () => {
        number++
    }

    return (
        <div>
            <div>
                <p> {number} </p>
            </div>
            <div>
                <button type="button" onClick={ suma} > plus  </button>
                <button type="button" onClick={ resta} > minus </button>
            </div>
        </div>
    );
}

export default Contador;
