import React from 'react';
import { useEffect } from 'react';
import db from "../../db.json"
import ItemCarrito from "./ItemCarrito"

function Carrito() {

    const carrito = db.Carrito.productos
    let totalCarrito;

    useEffect(() => {
        carrito.map((info) => {
            const id = info[0]
            const producto = carrito[id]


            console.log("id",id)

            console.log("soy carrito", carrito[id])

            console.log("soy la data", producto.precio)


            totalCarrito = totalCarrito + (info[1] *(producto.precio))
        } )
    }, []);

    return (
        <>
            <div>
                {carrito.map((info, i) => (
                    <div key={i}>
                        <ItemCarrito prenda={info} />
                    </div>
                ))}
            </div>
            <div>
                <p> precio total: {totalCarrito} </p>
            </div>
        </>
    );
}

export default Carrito;
