import Item from "./Item";
import React from "react";
import { Col, Row } from "react-bootstrap";
import { Link } from "react-router-dom";

const ItemList = (data) => {
    console.log("Estos son los productos", data.productos)

    const laRopa = data.productos

    /*     const loRetornable = laRopa.map((prenda) => {
            return (
                <Col md="4" mb="2">
                    <div>
                        <p> {prenda.nombre}</p>
                        <p> precio: {prenda.precio}$</p>
                        <img
                            className="remera"
                            src={prenda.imagen}
                        />
                        <br />
                        <button>ver detalel del producto</button>
                    </div>
                </Col>
            )
        }) */

    const protoductos = () => {
        return (
            <div>
                {data.map((prenda) => (
                    <Col>
                        <p> {prenda.nombre}</p>
                        <p> precio: {prenda.precio}$</p>
                        <img
                            className="remera"
                            src={prenda.imagen}
                        />
                        <br />
                        <button>ver detalle del producto</button>
                    </Col>
                ))}
            </div>
        )
    }

    return (
        <Row className="justify-content-md-center">
            {protoductos}
        </Row>
    )

}
export default ItemList;
