import React from 'react';
import { useState } from 'react';

function ItemCount() {

    const [number,setNumber] = useState(0)
    const [mensaje,setMensaje] = useState("usted puede agregar ó restar remeras")
    const [stockHay, setStockHay] = useState(true)
    const stock = 3;

    if(stock > 0)
    {
        setStockHay(true)
    }
    else{
        setStockHay(false)
    }

    const handleSuma = () => {
        if(number+ 1 <= 3)
        {
            setNumber(number + 1)
        }
        else {
            setMensaje("No se pude, supera el stock")
            setTimeout(() => {
                setMensaje("usted puede agregar ó restar remeras")
            }, 500)
        }
    }

    const handleResta = () => {
        if(number -1 >= 0)
        {
            setNumber(number - 1)
        }
        else{
            setMensaje("No se pude, no agregar remeras")
            setTimeout(() => {
                setMensaje("usted puede agregar ó restar remeras")
            }, 500)
        }
    }  

    return (
        <div style={ {backgroundcolor: "338ABF"} }>
            <p> Remera colorida</p>
            <div>
                <p> {number} </p>
            </div>
            <div>
                <button type="button"  onClick={ handleSuma } disabled={!stockHay}> plus  </button>
                <button type="button"  onClick={ handleResta }  disabled={!stockHay}> minus </button>
            </div>
            <p> {mensaje} </p>
        </div>
    );
}

export default ItemCount;
