import React from 'react';
import { useEffect } from 'react';
import db from "../../db.json"
import ItemCarrito from "./ItemCarrito"

function Carrito() {

    const carrito = db.Carrito.productos
    let totalCarrito;

    useEffect(() => {
        carrito.map((info) => {
            console.log("soy informacion",info)
            console.log("soy en info[1]",info[1])
            totalCarrito = totalCarrito + (info[1] *(productos[info].precio))
        } )
    }, []);

    return (
        <>
            <div>
                {carrito.map((info, i) => (
                    <div key={i}>
                        <ItemCarrito prenda={info} />
                    </div>
                ))}
            </div>
            <div>
                <p> precio total: {totalCarrito} </p>
            </div>
        </>
    );
}

export default Carrito;
