import React from "react";
import db from "../db.json"
import { Link } from "react-router-dom";
import { useState } from "react";

const ItemDetail = ({idPrenda}) => {

    const [prenda, setPrenda] = useState()
    const productos = db.Ropa
    const idProducto = idPrenda

    console.log("soy el id ------------>",(idProducto + 1) )

    for (let i = 0; i < productos.length; i++) {
        var product = productos[i]
        console.log("esto es un producto", product.id)
        if(product.id === idPrenda)
        {
            setPrenda(product)
        }
    }
    console.log("esto es prenda -------->",prenda)

    return (
        <div>
            <h2> {prenda.nombre}</h2>
            <p> Precio: {prenda.precio}$</p>
            <p> Stock: {prenda.stock}$</p>
            <p> Descripcion: {prenda.descripcion}$</p>
            <img
                className="remera"
                src={prenda.imagen}
            />
            <br />
        </div>
    )
}
export default ItemDetail;
