import React from 'react';
import { useState, createContext, useEffect } from 'react';
import db from "../../db.json"
import ItemCarrito from "./ItemCarrito"

const CartContext = createContext();

function Carrito() {

    /* const carrito = db.Carrito.productos
    const productos = db.Ropa
    let [totalCarrito, setTotalCarrito] = useState(0);

    useEffect(() => {
        carrito.map((info) => {
            const id = info[0]
            const producto = productos[id]
            setTotalCarrito(totalCarrito + (info[1] * (producto.precio)))
        })
    }, []);

    return (
        <>
            <div>
                {carrito.map((info, i) => (
                    <div key={i}>
                        <ItemCarrito prenda={info} />
                    </div>
                ))}
            </div>
            <div>
                <p> precio total: {totalCarrito} </p>
            </div>
        </>
    ); */


    const [cartItems, setCartItems] = useState(() => {
        try {
            const productosEnLocalStorage = localStorage.getItem("cartProducts");
            return productosEnLocalStorage ? JSON.parse(productosEnLocalStorage) : [];
        }
        catch (errpr) {
            return [];
        }
    });

    useEffect(() => {
        localStorage.setItem("cartProducts", JSON.stringify(cartItems))
    }, [cartItems]);

    const addItemToCart = (product) => {
        const inCart = cartItems.find(
            (productInCart) => productInCart.id === product.id
        )

        if (inCart) {
            setCartItems(
                cartItems.map((productInCart) => {
                    if (productInCart.id === product.id) {
                        return { ...inCart, amount: inCart.amount + 1 }
                    }
                    else return productInCart;
                })
            )
        }
        else {
            setCartItems([...cartItems, { ...product, amount: 1 }])
        }
    }

    const deleteItemToCart = (product) => {
        const inCart = cartItems.find(
            (productInCart) => productInCart.id === product.id
        );

        if (inCart.amount === 1) {
            setCartItems(
                cartItems.filter(productInCart => productInCart => productInCart.id !== product.id)
            )
        } else {
            setCartItems((productInCart) => {
                if (productInCart.id === product.id) {
                    return { ...inCart, amount: inCart.amount - 1 }
                }
                else return productInCart;
            })
        }
    }



    return(
        <CartContext.Provider value={(cartItems, addItemToCart, deleteItemToCart)}>
            {}
        </CartContext.Provider>
    )
}

export default Carrito;
