import React from 'react';


function Contador() {

    const [number,] = 0

    const suma = () => {
        console.log("Estoy sumando")
        ++number
        console.log("numero",number)
    }

    const resta = () => {
        console.log("Estoy restando")
        --number
    }

    return (
        <div>
            <div>
                <p> {number} </p>
            </div>
            <div>
                <button type="button" onClick={ suma} > plus  </button>
                <button type="button" onClick={ resta} > minus </button>
            </div>
        </div>
    );
}

export default Contador;
